import React from 'react'
import { CardContent } from '@material-ui/core'

import { Card } from 'material-ui'
import { Link } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import Typography from 'material-ui/styles/typography';
import { withStyles } from '@material-ui/core/styles'



class Screen0 extends React.Component{

    render(){
         const {classes,handleBackScreenChange} = this.props
        return(
          <div className={classes.mainContainer}>
              <div >
                  <Typography className = {classes.mainHeading}>
                   WHAT'S YOUR TRADE-IN  WORTH?
                   </Typography>
              </div>
              <div >
                  <Typography className ={classes.subHeading}>
                  Get more  than an estimate - get a virtual appraisal and the confidence that comes <br/> from knowing and the end valuation is backed by the Ford.
                  Valuation  is appliable towards <br/> the purchase of a New or Certified Pre-Owned Ford or Lincoln vehicle.
                  </Typography>
              </div>

              <div>
              <Card>
              <CardContent>
                    <div className ={classes.topLeftCardContentHeading}>
                        I OWN IT
                    </div>
                    <div className={classes.topLeftCardContentSubHeading}>
                        my vehicle is fully paid off 
                    </div>

                  <Button color='primary' onClick={()=>handleBackScreenChange('screen1')} >Select</Button>


              </CardContent> 
              <CardContent>
                    <div className ={classes.topRightCardContentHeading}>
                        I AM FINANCING
                    </div>
                    <div className={classes.topRightCardContentSubHeading}>
                      I am financing through Ford Credit,<br/> another bank or Credit union.
                    </div> 

                  < Button color='primary' onClick={()=>handleBackScreenChange('screen1')} >Select</Button>


              </CardContent> 
              </Card>


              </div> 



              <div className={classes.bottomText}>
                  if you have a current lease,please  <Link className={`  ${classes.ContactYourDealer}`}  type="submit" variant="contained" onClick={()=>handleBackScreenChange('dealerpage')}>Contact Your Dealer</Link>
                  for more <br/> information about trading in your vehicle.

              </div>

              <div className={classes.policy}>
                  See Trade-in Terms & Conditions for eligibility details and restrictions.
              </div>

          </div>

        )
    }
}

const styles = theme => (
    {
        mainContainer: {
            padding: '50px 80px ',
            [theme.breakpoints.down('sm')]: { 
                padding: '20px'
              },
            },
        mainHeading: {
                //fontFamily
                  fontSize: '60px',
                   height:'70px',
                  lineHeight: '1.4',
                  [theme.breakpoints.down('sm')]: { 
                    fontSize: '30px',
                    lineHeight: '1.2',
                  },
                  fontWeight: '200',
                  letterSpacing: '2px',
                  fontFamily:"FordAntennaCond",
                  color: '#4d4d4d',
              },
        subHeading: {
                width: '10px',
                height: '27px',
                fontFamily: 'FordAntenna',
                fontSize: '17px',
                fontWeight: 'normal',
                fontStretch: 'normal',
                fontStyle: 'normal',
                lineHeight: '0.5',
                letterSpacing: '1px',
                textAlign: 'center',
                color: '#4d4d4d',
                },
        topLeftCardContentHeading:{

                },
        topLeftCardContentSubHeading:{

        },
        topRightCardContentHeading:{

        },
        topRightCardContentSubHeading:{

        },
        bottomText:{

        },
        policy:{

        }

}
)

export default withStyles (styles)(Screen0)
